import React from "react";

export default function TextForm(props) {
  return (
    <>
      <div class="mb-3">
        <h1>{props.heading}</h1>
        <label for="exampleFormControlTextarea1" class="form-label">
        </label>
        <textarea class="form-control"  id="exampleFormControlTextarea1"  rows="3"/>
      </div>
    </>
  );
}
