import React ,{useState} from "react";
export default function TextForm(props) {
  const upperCase=()=>{
    console.log("Converting text to upper case "+text);
    let newText=text.toUpperCase();
    settext(newText);
  }
  const lowerCase=()=>{
    console.log("Converting text to lower case");
    let newText=text.toLowerCase();
    settext(newText);
  }
  const clean=()=>{
    console.log("CLEARING....");
  }
  const handleOnChange=(event)=>{
    console.log("handleOnChange");
    settext(event.target.value);
  }
  const [text, settext] = useState('Enter text here');
  return (
    <>
      <div className="mb-3">

        <h1>{props.heading}</h1>
        <label htmlFor="mybox" className="form-label">
        </label>
        <textarea className="form-control" onChange={handleOnChange} value={text} id="mybox"  rows="8"/>

      </div>


      <div className="btn-group" role="group" aria-label="Basic example">
        <button type="button" onClick={upperCase} className="btn btn-outline-primary">Convert to UPPER case</button>
        <button type="button" onClick={lowerCase} className="btn btn-outline-primary">Convert to lower case</button>
        <button type="button" onClick={clean} className="btn btn-outline-primary">Clear</button>
    </div>
    </>
  );
}
