import React ,{useState} from "react";
export default function TextForm(props) {
  const upperCase=()=>{
    settext1(text.toUpperCase());
  }
  const lowerCase=()=>{
    settext1(text.toLowerCase());
  }
  const clean=()=>{
    settext("");
  }
  const handleOnChange=(event)=>{
    settext(event.target.value);
  }
  const [text, settext] = useState('');
  const[text1,settext1] = useState("");
  return (
    <>
      <div className="mb-3">

        <h1>{props.heading}</h1>
        <label htmlFor="mybox" className="form-label">
        </label>
        <div className="input-group">
          <textarea className="form-control" placeholder="Enter Text Here..." onChange={handleOnChange} value={text} id="mybox"  rows="10"/>
          <textarea className="form-control" placeholder="Converted Text Here..." onChange={handleOnChange} value={text1} id="mybox"  rows="10"/>
        </div>

      </div>


      <div className="btn-group" role="group" aria-label="Basic example">
        <button type="button" onClick={upperCase} className="btn btn-outline-primary">Convert to UPPER case</button>
        <button type="button" onClick={lowerCase} className="btn btn-outline-primary">Convert to lower case</button>
        <button type="button" onClick={clean} className="btn btn-outline-primary">Clear</button>
    </div>
    <div className="container my-3">
      <h2>Your text Summary</h2>
      <p>{text.length} characters and {text.split(" ").length+1} words</p>
      <p>{text}</p>
    </div>
    </>
  );
}
