import React ,{useState} from "react";
export default function TextForm(props) {
  const upperCase=()=>{
    settext1(text.toUpperCase());
  }
  const lowerCase=()=>{
    settext1(text.toLowerCase());
  }
  const clean=()=>{
    settext("");
    settext1("");
  }
  const handleOnChange=(event)=>{
    settext(event.target.value);
  }
  const CopyText=()=>{
    let text=document.getElementById("mybox1");
    text.select();
    navigator.clipboard.writeText(text.value);
  }
  const SpaceRemove=()=>{
    settext1(text.split(/[ ]+/).join(" "));//regex
  }
  const [text, settext] = useState('');
  const[text1,settext1] = useState("");
  return (
    <>
      <div className="mb-3">

        <h1>{props.heading}</h1>
        <label htmlFor="mybox" className="form-label">
        </label>
        <div className="input-group">
          <textarea className="form-control mx-1" placeholder="Enter Text Here..." onChange={handleOnChange} value={text} id="mybox"  rows="10"/>
          <textarea className="form-control mx-1" placeholder="Converted Text Here..." onChange={handleOnChange} value={text1} id="mybox1"  rows="10"/>
        </div>

      </div>


      <div className="btn-group" role="group" aria-label="Basic example">
        <button type="button" onClick={upperCase} className="btn btn-outline-primary mx-1">Convert to UPPER case</button>
        <button type="button" onClick={lowerCase} className="btn btn-outline-primary mx-1">Convert to lower case</button>
        <button type="button" onClick={clean} className="btn btn-outline-primary mx-1">Clear</button>
        <button type="button" onClick={CopyText} className="btn btn-outline-primary mx-1">Copy Text</button>
        <button type="button" onClick={SpaceRemove} className="btn btn-outline-primary mx-1">Remove extra spaces</button>
    </div>
    <div className="container my-3">
      <h2>Your text Summary</h2>
      <p>{text.length} characters and {text.split(" ").length} words</p>
      <p>You will need {0.008*text.split(" ").length} minutes to read.</p>
      <p>{text}</p>
    </div>
    </>
  );
}
