import React from "react";

export default function TextForm(props) {
  return (
    <>
      <div class="mb-3">
        <h1>{props.heading}</h1>
        <label for="mybox" class="form-label">
            Enter text
        </label>
        <textarea class="form-control"  id="mybox"  rows="8"/>

      </div>
      <button className="btn btn-primary">Convert to UPPER case</button>
    </>
  );
}
