import React ,{useState} from "react";
export default function TextForm(props) {
  return (
    <>
      <div className="mb-3">
        <h1>{props.heading}</h1>
        <label htmlFor="mybox" className="form-label">
        </label>
        <textarea className="form-control" value={text} id="mybox"  rows="8"/>

      </div>


      <div className="btn-group" role="group" aria-label="Basic example">
        <button type="button" className="btn btn-outline-primary">Convert to UPPER case</button>
        <button type="button" className="btn btn-outline-primary">Convert to lower case</button>
        <button type="button" className="btn btn-outline-primary">Clear</button>
    </div>
    </>
  );
}
